	projects1 = projects1.map(p => ({
		...p,
		thumbnail: {
			...p.thumbnail,
			src: p.root + p.thumbnail.src,
		},
		imgs: p.imgs.map(i => ({ ...i, src: p.root + i.src })),
	}))

	function openPhotoSwhipe(id: string) {
		const { imgs } = projects1.find(p => p.id === id)

		const gallery = new PhotoSwipe({
			gallery: '#pswp-gallery-id',
			dataSource: imgs,
			pswpModule: () => import('photoswipe'),
			loop: false,
			bgOpacity: 0.9,
			showAnimationDuration: 100,
			hideAnimationDuration: 100,
			pinchToClose: false,
		})
		gallery.init()
	}
