	src={projects1.find(p => p.id === id).thumbnail.src}
	width={projects1.find(p => p.id === id).thumbnail.width}
	height={projects1.find(p => p.id === id).thumbnail.height}
	alt={id}
	on:keydown={() => {
		/**/
	}}
	on:click={() => openPhotoSwhipe(id)} />

<style>
	.project {
		transition: all 0.08s ease-in-out;
